CREATE PROC [dbo].[sp_vdt_get_nhathau_hopdong_by_goithau] 
	@goiThauId uniqueidentifier
	
AS
BEGIN
	SELECT 
	hd.Id as IIdHopDongId,
	hdnt.iID_GoiThauID as IIdGoiThauId,
	hdnt.iID_NhaThauID as IIdNhaThauId,
	hd.sSoHopDong as SSoHopDong,
	nt.sTenNhaThau as STenNhaThau,
	lhd.sTenLoaiHopDong as SHinhThucHopDong,
	hdnt.fGiaTri as FGiaTri,
	hd.dNgayHopDong as DNgayHopDong
		
	FROM
		VDT_DA_HopDong_GoiThau_NhaThau hdnt
		LEFT JOIN VDT_DA_TT_HopDong hd ON hd.Id = hdnt.iID_HopDongID
		LEFT JOIN VDT_DM_NhaThau nt ON nt.Id = hdnt.iID_NhaThauID 
		INNER JOIN VDT_DM_LoaiHopDong lhd on lhd.iID_LoaiHopDongID = hd.iID_LoaiHopDongID
		
	WHERE
			
		hdnt.iID_GoiThauID = @goiThauId
		AND hd.Id  is not null

END;
GO

