CREATE PROCEDURE [dbo].[sp_bh_cap_phat_bo_sung_chi_tiet]
	@IDChungTu uniqueidentifier
AS
BEGIN
	SELECT 
		info.iID_CTCapPhatBSChiTiet,
		info.iID_CTCapPhatBS,
		info.bHangCha,
		info.iID_MaDonVi,
		info.iID_MLNS,
		info.iID_MLNS_Cha,
		info.iNamLamViec,
		info.sXauNoiMa,
		info.sLNS,
		info.sCoSoYTe,
		info.fDaQuyetToan,
		info.fDaCapUng,
		info.fThuaThieu,
		info.fSoCapBoSung,
		info.sTenDonVi,
		info.dNgaySua,
		info.dNgayTao,
		info.sNguoiSua,
		info.sNguoiTao,
		info.sGhiChu,
		info.iID_CoSoYTe,
		info.iID_MaCoSoYTe
	FROM
	(
		select cpbs.iID_CTCapPhatBS,
		ctct.iID_CTCapPhatBSChiTiet,
		ctct.bHangCha,
		ctct.iID_MaDonVi,
		ctct.iID_MLNS,
		ctct.iID_MLNS_Cha,
		ctct.iNamLamViec,
		ctct.sXauNoiMa,
		ctct.sLNS,
		cpbs.sCoSoYTe,
		ctct.fDaQuyetToan,
		ctct.fThuaThieu,
		ctct.fSoCapBoSung,
		ctct.fDaCapUng,
		ctct.sTenDonVi,
		ctct.dNgaySua,
		ctct.dNgayTao,
		ctct.sNguoiSua,
		ctct.sNguoiTao,
		ctct.sGhiChu,
		ctct.iID_CoSoYTe,
		ctct.iID_MaCoSoYTe
		FROM
		(SELECT 
			ct.iID_CTCapPhatBS,
			ct.sCoSoYTe
			FROM
			BH_CP_CapBoSung_KCB_BHYT ct
			WHERE ct.iID_CTCapPhatBS = @IDChungTu
			GROUP BY
			ct.iID_CTCapPhatBS,
			ct.sCoSoYTe,
			ct.iQuy) AS cpbs
		JOIN
		BH_CP_CapBoSung_KCB_BHYT_ChiTiet ctct ON cpbs.iID_CTCapPhatBS = ctct.iID_CTCapPhatBS
	) info

END
GO
