CREATE PROCEDURE [dbo].[sp_bh_cap_phat_bo_sung_tao_ct_tonghop]
	@ListIdChungTuTongHop ntext,
	@IdChungTu nvarchar(100),
	@NamLamViec int
AS
BEGIN
	INSERT INTO BH_CP_CapBoSung_KCB_BHYT_ChiTiet (
		iID_CTCapPhatBS,
		iNamLamViec,
		fDaQuyetToan,
		fDaCapUng,
		fThuaThieu,
		fSoCapBoSung,
		sXauNoiMa,
		sLNS,
		iID_CoSoYTe,
		iID_MaCoSoYTe,
		iID_MLNS
		)
		SELECT @idChungTu,
			@NamLamViec,
			sum(fDaQuyetToan),
			sum(fDaCapUng),
			sum(fThuaThieu),
			sum(fSoCapBoSung),
			sXauNoiMa,
			sLNS,
			iID_CoSoYTe,
			iID_MaCoSoYTe,
			iID_MLNS
		FROM BH_CP_CapBoSung_KCB_BHYT_ChiTiet
		WHERE iID_CTCapPhatBS in
			(SELECT *
			 FROM f_split(@ListIdChungTuTongHop))
			 GROUP BY 
				sXauNoiMa,
				sLNS,
				iID_CoSoYTe,
				iID_MaCoSoYTe,
				iID_MLNS

	--danh dau chung tu da tong hop
	update BH_CP_CapBoSung_KCB_BHYT set bDaTongHop = 1 
	where iID_CTCapPhatBS in
		(SELECT *
		 FROM f_split(@ListIdChungTuTongHop));
END
